padraoregex = '^[A-Z]{2}[1-9]{9}[A-Z]{2}$' #padrão regex do código dos Correios

entrada = 'V123456789BR' #entrada que vai verificar se esse código pertence ao padrão regex 

validade = re.search(padraoregex, entrada) #re.search, função python que avalia se a entrada pertence ao regex, e retorna um boolean

if validade: #se for verdadeiro
  servico = entrada[0:2] # a partir das duas primeiras siglas da entrada é possível verificar qual a Classificação da postagem
  if dicionario_sigla.get(servico): #se no dicionario acima, tiver a sigla que achamos na linha 71
    paisorigem = entrada[11:] #as duas ultimas letras da entrada se referem a Classificação do país de origem da postagem
    if paisorigem == 'BR': #se for BR veio do Brasil
      print(f"Código do Correio Válido\nClassificação: {dicionario_sigla[servico]}\nPaís de Origem: Brasil") #printa a classificação e se é do Brasil
    else:
      print(f"Código do Correio Válido\nClassificação: {dicionario_sigla[servico]}\nPaís de Origem: Exterior") #printa a classificação e não especifica a origem, que é estrangeira
  else: #se a sigla (da variavel servico) não for encontrada no dicionario, o código é um código que se enquadra no padrão regex dos Correios, porém não pertence às Classificações dos Correios
    print(f"Código segue o Padrão Regex dos Correios \nPorém sem Sigla Correspondente")
else:
  print("Código de Entrada Inválido") #se "validade" for falso, código de entrada não pertence ao regex dos Correios